import XCTest
@testable import Bitcoin

final class BitcoinCoreTaprootTests: XCTestCase {

    func testVectors() {
        for testCase in coreTestAssets {

            var includeFlags = Set(testCase.flags.split(separator: ","))
            includeFlags.remove("NONE")
            let config = ScriptConfigurarion(
                strictDER: includeFlags.contains("DERSIG"),
                pushOnly: includeFlags.contains("SIGPUSHONLY"),
                minimalData: includeFlags.contains("MINIMALDATA"),
                lowS: includeFlags.contains("LOW_S"),
                cleanStack: includeFlags.contains("CLEANSTACK"),
                nullDummy: includeFlags.contains("NULLDUMMY"),
                strictEncoding: includeFlags.contains("STRICTENC"),
                payToScriptHash: includeFlags.contains("P2SH"),
                checkLockTimeVerify: includeFlags.contains("CHECKLOCKTIMEVERIFY"),
                checkSequenceVerify: includeFlags.contains("CHECKSEQUENCEVERIFY"),
                discourageUpgradableNoOps: true,
                constantScriptCode: includeFlags.contains("CONST_SCRIPTCODE"),
                witness: includeFlags.contains("WITNESS"),
                witnessCompressedPublicKey: true,
                minimalIf: true,
                nullFail: includeFlags.contains("NULLFAIL"),
                discourageUpgradableWitnessProgram: includeFlags.contains("DISCOURAGE_UPGRADABLE_WITNESS_PROGRAM"),
                taproot: includeFlags.contains("TAPROOT"),
                discourageUpgradableTaprootVersion: true,
                discourageOpSuccess: true,
                discourageUpgradablePublicKeyType: true
            )
            let allOff = ScriptConfigurarion.init(strictDER: false, pushOnly: false, minimalData: false, lowS: false, cleanStack: false, nullDummy: false, strictEncoding: false, payToScriptHash: false, checkLockTimeVerify: false, checkSequenceVerify: false, discourageUpgradableNoOps: false, constantScriptCode: false, witness: false, witnessCompressedPublicKey: false, minimalIf: false, nullFail: false, discourageUpgradableWitnessProgram: false, discourageOpSuccess: false, discourageUpgradablePublicKeyType: false)

            let unsignedTx = BitcoinTransaction(Data(hex: testCase.tx)!)!
            let previousOutputs = testCase.previousOutputs.map { TransactionOutput(Data(hex: $0)!)! }
            let inputIndex = testCase.inputIndex
            let input = unsignedTx.inputs[inputIndex]
            if let success = testCase.success {
                let successInput = TransationInput(
                    outpoint: input.outpoint,
                    sequence: input.sequence,
                    script: .init(Data(hex: success.scriptSig)!),
                    witness: .init(success.witness.map { Data(hex: $0)! })

                )
                var successInputs = unsignedTx.inputs
                successInputs[inputIndex] = successInput
                let successTx = BitcoinTransaction(version: unsignedTx.version, locktime: unsignedTx.locktime, inputs: successInputs, outputs: unsignedTx.outputs)
                XCTAssertNoThrow(try successTx.verifyScript(inputIndex: inputIndex, previousOutputs: previousOutputs, configuration: testCase.final ? config : allOff))
            }
            if let failure = testCase.failure, testCase.final {
                let failureInput = TransationInput(
                    outpoint: input.outpoint,
                    sequence: input.sequence,
                    script: .init(Data(hex: failure.scriptSig)!),
                    witness: .init(failure.witness.map { Data(hex: $0)! })

                )
                var failureInputs = unsignedTx.inputs
                failureInputs[inputIndex] = failureInput
                let failureTx = BitcoinTransaction(version: unsignedTx.version, locktime: unsignedTx.locktime, inputs: failureInputs, outputs: unsignedTx.outputs)
                XCTAssertThrowsError(try failureTx.verifyScript(inputIndex: inputIndex, previousOutputs: previousOutputs, configuration: .standard))
            }
        }
    }
}

fileprivate struct TestVector {

    init(tx: String, previousOutputs: [String], inputIndex: Int, success: TestVector.Result? = nil, failure: TestVector.Result? = nil, flags: String, final: Bool = false, comment: String) {
        self.tx = tx
        self.previousOutputs = previousOutputs
        self.inputIndex = inputIndex
        self.success = success
        self.failure = failure
        self.flags = flags
        self.final = final
        self.comment = comment
    }


    struct Result {
        let scriptSig: String
        let witness: [String]
    }

    let tx: String
    let previousOutputs: [String]
    let inputIndex: Int
    let success: Result?
    let failure: Result?
    let flags: String
    let final: Bool
    let comment: String
}

